#include <windows.h>\n#include <shlobj.h>\n\n__declspec(dllexport) int MoveToRecycleBin(const char* filePath) {\n    SHFILEOPSTRUCT sf = {0};\n    TCHAR szFrom[MAX_PATH];\n    \n    // Convert the file path from ANSI to Unicode (TCHAR)\n    MultiByteToWideChar(CP_ACP, 0, filePath, -1, szFrom, MAX_PATH);\n    PathAddBackslash(szFrom); // Append a backslash at the end\n\n    sf.hwnd = NULL;\n    sf.wFunc = FO_DELETE;\n    sf.pFrom = szFrom;\n    sf.fFlags = FOF_ALLOWUNDO | FOF_NOCONFIRMATION | FOF_SILENT;\n\n    return (SHFileOperation(&sf) == 0);\n}