#include <ctime>\n#include <iostream>\n#include <iomanip>\n#include <sstream>\n\nstd::string timeToString(time_t &seconds) {\n    char buffer[80];\n    struct tm *timeinfo;\n    timeinfo = localtime(&seconds);\n    strftime(buffer, sizeof(buffer), "%Y-%m-%d %H:%M:%S", timeinfo);\n    return std::string(buffer);\n}\n\nint main() {\n    time_t seconds;\n    time(&seconds);\n\n    std::cout << "Epoch Time: " << seconds << std::endl;\n    std::string s = timeToString(seconds);\n    std::cout << "Readable Time: " << s << std::endl;\n\n    return 0;\n}