#include <windows.h>\n#include <iostream>\n\nint main() {\n    HKEY hKey;\n    LONG result = RegOpenKeyEx(HKEY_CURRENT_USER,\n                                TEXT("Software\\MyApp"),\n                                0, KEY_SET_VALUE, &hKey);\n\n    if (result == ERROR_SUCCESS) {\n        DWORD newValue = 123456;\n        result = RegSetValueEx(hKey, TEXT("ValueName"), 0, REG_DWORD,\n                               reinterpret_cast<const BYTE*>(&newValue),\n                               sizeof(newValue));\n\n        if (result == ERROR_SUCCESS) {\n            std::cout << "Value written successfully." << std::endl;\n        } else {\n            std::cerr << "Failed to write value. Error: " << result << std::endl;\n        }\n\n        RegCloseKey(hKey);\n    } else {\n        std::cerr << "Failed to open key. Error: " << result << std::endl;\n    }\n\n    return 0;\n}