#include <iostream>\n\n#if __GNUC__ < 3\n    #include <strstream>\n    typedef std::strstream StringStreamType;\n#else\n    #include <sstream>\n    typedef std::stringstream StringStreamType;\n#endif\n\n// Helper function to create a stringstream with initial content\nStringStreamType createStringStream(const char* init) {\n    StringStreamType ss;\n    if (init) ss << init;\n    return ss;\n}\n\nint main() {\n    // Create a string stream and add some text\n    StringStreamType str = createStringStream("Hello World");\n    \n    #if __GNUC__ < 3\n        std::cout << str.str() << std::endl; // Need to use str.str() to get the underlying char*\n    #else\n        std::cout << str.str() << std::endl;\n    #endif\n\n    return 0;\n}