#include <QCoreApplication>\n#include <QProcess>\n#include <QDebug>\n\nint main(int argc, char *argv[]) {\n    QCoreApplication app(argc, argv);\n\n    QProcess process;\n    QObject::connect(&process, &QProcess::finished, [&process]() {\n        qDebug() << "Crasher finished with exit code" << process.exitCode();\n    });\n\n    qDebug() << "Starting crasher process...";\n    process.start("./crasher");\n    process.waitForFinished(-1); // Wait until the process ends\n\n    return app.exec();\n}