#include <windows.h>\n#include <iostream>\n\nint main() {\n    if (!SetDllDirectory(L"C:\\Path\\To\\Your\\DLL")) {\n        std::cerr << "Failed to set DLL directory. Error: " << GetLastError() << std::endl;\n        return 1;\n    }\n\n    // Launch the application that needs the modified DLL search path\n    ShellExecute(NULL, L"open", L"C:\\Path\\To\\Your\\Application.exe", NULL, NULL, SW_SHOWNORMAL);\n    return 0;\n}