#include <unistd.h>\n#include <sys/types.h>\n\nvoid reloadSelf() {\n    pid_t pid = fork();\n    if (pid == 0) { // Child process\n        execl("/etc/init.d/foo", "foo", "restart", nullptr);\n        _exit(EXIT_FAILURE); // In case exec fails\n    } else if (pid > 0) { // Parent process\n        _exit(EXIT_SUCCESS); // Exit parent after forking\n    } else {\n        // Fork failed, handle error\n        perror("fork");\n    }\n}