#include <iostream>\n#include <signal.h>\n\nvoid handleSignal(int sig) {\n    std::cout << "Caught signal: " << sig << ", cleaning up..." << std::endl;\n    // Perform cleanup here\n}\n\nint main() {\n    // Register the signal handler\n    signal(SIGTERM, handleSignal);\n    signal(SIGINT, handleSignal);  // Also catch Ctrl+C\n\n    std::cout << "Running... Press Ctrl+C to terminate gracefully." << std::endl;\n\n    while (true) {\n        // Simulate work\n    }\n\n    return 0;\n}