#include <comdef.h>\n#include <excel.h>\n\nint main() {\n    CoInitialize(NULL);\n    \n    try {\n        // Create an instance of the Excel application.\n        _ApplicationPtr app("Excel.Application");\n        app->Visible = false;\n\n        // Add a new workbook.\n        WorkbooksPtr workbooks(app->Workbooks);\n        _WorkbookPtr workbook(workbooks->Add());\n\n        // Get the first worksheet.\n        WorksheetsPtr sheets(workbook->Worksheets);\n        WorksheetPtr sheet(sheets->Item[1]);\n\n        // Use the sheet as needed...\n        \n    } catch (_com_error &e) {\n        wprintf(L"Error: %s\n", e.ErrorMessage());\n    }\n\n    CoUninitialize();\n    return 0;\n}