#include <iostream>\n#include <string>\n\nint main() {\n    int x, y;\n    std::cout << "Enter first number: ";\n    std::cin >> x;\n    std::cout << "Enter second number: ";\n    std::cin >> y;\n\n    // Constructing the command to run the other program with arguments\n    std::string command = "C:\\Path\\To\\Your\\Program2.exe " + std::to_string(x) + " " + std::to_string(y);\n\n    // Running Program 2 from here using system()\n    int status = std::system(command.c_str());\n\n    if (status == 0)\n        std::cout << "Addition program executed successfully." << std::endl;\n    else\n        std::cerr << "Failed to execute addition program." << std::endl;\n\n    return 0;\n}