#include <windows.h>\n#include <tchar.h>\n#include <iostream>\n\nvoid DumpMappedLogicalDrives() {\n    TCHAR szDevices[MAX_PATH];\n    DWORD result = QueryDosDevice(NULL, szDevices, sizeof(szDevices) / sizeof(TCHAR));\n\n    if (result == 0) {\n        _tprintf(_T("QueryDosDevice failed with error: %lu\n"), GetLastError());\n        return;\n    }\n\n    for (LPTSTR p = szDevices; *p != _T('\0'); p += _tcslen(p) + 1) {\n        if (_tcsnicmp(p, _T("\\??\\"), 4) == 0) {\n            TCHAR szDrive[3] = { p[4], _T(':'), _T('\0') };\n            LPTSTR pTarget = p + 5;\n            _tprintf(_T("Logical Drive: %s -> Device Path: \\%s\n"), szDrive, pTarget);\n        }\n    }\n}\n\nint main() {\n    DumpMappedLogicalDrives();\n    return 0;\n}