// dll/Direct3DSprite.cpp\n#include "base/Sprite.h"\n#include <windows.h>\n\nclass Direct3DSprite : public base::Sprite {\n    // Implement Sprite interface using Direct3D\npublic:\n    Texture* GetTexture() override;\n    unsigned GetWidth() override;\n    // ...\n};\n\nextern "C" __declspec(dllexport) base::Sprite* CreateSprite(base::Texture* texture) {\n    return new Direct3DSprite(texture);\n}\n\nextern "C" __declspec(dllexport) void DestroySprite(base::Sprite* sprite) {\n    delete sprite;\n}