#include <comdef.h>\n#import ".\YourCSharpLibrary.tlb" // Import your C# library\n\nvoid InitializeModel(char* modelType)\n{\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr))\n    {\n        // Handle error\n        return;\n    }\n\n    IUnitModelPtr pIUnit = NULL;\n\n    if (_strcmpi(modelType, "Model1") == 0) \n    {\n        hr = pIUnit.CreateInstance(__uuidof(ClassOne));\n    }\n    else if (_strcmpi(modelType, "Model2") == 0)\n    {\n        hr = pIUnit.CreateInstance(__uuidof(ClassTwo));\n    }\n\n    // Check for success in creating an instance\n    if (SUCCEEDED(hr))\n    {\n        // Call methods on the COM object\n        pIUnit->Method1();\n        pIUnit->Method2();\n    }\n    else\n    {\n        // Handle error\n    }\n\n    CoUninitialize(); // Uninitialize COM\n}