// Original Loop\nfor (int i = 0; i < n; ++i) {\n    result += array[i];\n}\n\n// Manually Unrolled Version\nfor (int i = 0; i <= n - 4; i += 4) { // Assuming n is a multiple of 4 for simplicity\n    result += array[i];\n    result += array[i + 1];\n    result += array[i + 2];\n    result += array[i + 3];\n}\n// Handle remaining elements if needed\nfor (; i < n; ++i) {\n    result += array[i];\n}