#include <iostream>\n#include <sys/types.h>\n#include <sys/stat.h>\n#include <cerrno>\n#include <cstring>\n\nint main() {\n    const char *path = "/home/shree/path/def";\n    struct stat path_stat;\n\n    if (stat(path, &path_stat) != 0) {\n        std::cerr << "Error: " << strerror(errno) << '\n';\n        return 1;\n    }\n\n    if (S_ISDIR(path_stat.st_mode)) {\n        std::cout << path << " is a directory.\n";\n    } else if (S_ISREG(path_stat.st_mode)) {\n        std::cout << path << " is a regular file.\n";\n    } else {\n        std::cout << path << " is neither a regular file nor a directory.\n";\n    }\n\n    return 0;\n}