#include <windows.h>\n#include <iostream>\n\nvoid ModifyRegistryValue() {\n    HKEY hKey;\n    DWORD dwResult = RegOpenKeyEx(HKEY_CURRENT_USER,\n                                   L"Software\\Microsoft\\Windows\\CurrentVersion\\Policies\\Explorer",\n                                   0, KEY_QUERY_VALUE | KEY_SET_VALUE, &hKey);\n    if (dwResult != ERROR_SUCCESS) {\n        std::cerr << "Failed to open registry key." << std::endl;\n        return;\n    }\n\n    DWORD dwType = REG_DWORD;\n    DWORD dwValue = 0;\n    DWORD cbData = sizeof(dwValue);\n    dwResult = RegQueryValueEx(hKey, L"NoDriveTypeAutoRun", NULL, &dwType, (LPBYTE)&dwValue, &cbData);\n    if (dwResult != ERROR_SUCCESS) {\n        std::cerr << "Failed to query value from registry." << std::endl;\n        RegCloseKey(hKey);\n        return;\n    }\n\n    const DWORD mask = 0x20;\n    if ((dwValue & mask) == mask) {\n        dwValue -= mask;\n        dwResult = RegSetValueEx(hKey, L"NoDriveTypeAutoRun", 0, REG_DWORD, (CONST BYTE*)&dwValue, sizeof(dwValue));\n        if (dwResult != ERROR_SUCCESS) {\n            std::cerr << "Failed to set value in registry." << std::endl;\n        } else {\n            std::cout << "Successfully updated the registry value." << std::endl;\n        }\n    }\n\n    RegCloseKey(hKey);\n}\n\nint main() {\n    ModifyRegistryValue();\n    return 0;\n}