#include <sys/time.h>\n#include <iostream>\n\nusing namespace std;\n\nconst uint64_t MULT_INV_1M = 78125; // 19 bits: inverse of 1,000,000 rounded up to nearest integer\n\nint main()\n{\n    hrtime_t start;\n    hrtime_t tmp1;\n    hrtime_t tmp2;\n    hrtime_t fin;\n\n    start = gethrtime();\n    tmp1 = (start / 1000000); // Direct division\n    fin = gethrtime();\n\n    cout << "Method 1" << endl;\n    cout << "Original val: " << start << endl;\n    cout << "Computed: " << tmp1 << endl;\n    cout << "Time:" << fin - start << endl;\n\n    start = gethrtime();\n    // Fixed-point arithmetic approximation: multiply by inverse (scaled) and shift right\n    tmp2 = (start * MULT_INV_1M) >> 39; // 39 = log2(1000000) rounded up\n    fin = gethrtime();\n\n    cout << "Method 2" << endl;\n    cout << "Original val: " << start << endl;\n    cout << "Computed: " << tmp2 << endl;\n    cout << "Time:" << fin - start << endl;\n\n    return 0;\n}