#include <windows.h>\n#include <iostream>\n\nint main() {\n    HANDLE hStdout = GetStdHandle(STD_OUTPUT_HANDLE);\n    if (hStdout == INVALID_HANDLE_VALUE) {\n        std::cerr << "Cannot get handle to standard output" << std::endl;\n        return 1;\n    }\n\n    SMALL_RECT windowSize;\n    COORD coord;\n\n    // Set the screen buffer dimensions\n    coord.X = 120;  // Columns\n    coord.Y = 3000; // Rows, must be greater than the height of the Window Size Y\n\n    if (!SetConsoleScreenBufferSize(hStdout, coord)) {\n        std::cerr << "Failed to set screen buffer size" << std::endl;\n        return 1;\n    }\n\n    // Set the window dimensions\n    windowSize.Left = 0;\n    windowSize.Top = 0;\n    windowSize.Right = 119; // Columns - 1 (new width - 1)\n    windowSize.Bottom = 25;  // New height - 1\n\n    if (!SetConsoleWindowInfo(hStdout, TRUE, &windowSize)) {\n        std::cerr << "Failed to set console window info" << std::endl;\n        return 1;\n    }\n\n    std::cout << "Console buffer and window dimensions adjusted successfully." << std::endl;\n\n    return 0;\n}