#include <iostream>\n#include <string>\n\nint main() {\n    // Define an array of 50 strings, each with 4 characters\n    std::string strings[50] = {"abcd", "efgh", "ijkl", "mnop", "qrst", "uvwx", "yzab",\n                               "cdef", "ghij", "klmn", "opqr", "stuv", "wxyz", "abdc",\n                               "efgd", "ihgf", "mnlk", "ponm", "rqpo", "vuts", "xzyb",\n                               // Fill the rest of the array similarly\n                               "abcd", "efgh", "ijkl", "mnop", "qrst", "uvwx", "yzab",\n                               "cdef", "ghij", "klmn", "opqr", "stuv", "wxyz", "abdc",\n                               "efgd", "ihgf", "mnlk", "ponm", "rqpo", "vuts", "xzyb"};\n\n    // Loop through each string in the array\n    for (const auto& str : strings) {\n        bool hasOAE = false;\n        \n        // Check if the current string contains 'o', 'a', or 'e'\n        for (char c : str) {\n            if (c == 'o' || c == 'a' || c == 'e') {\n                hasOAE = true;\n                break; // No need to check further if found\n            }\n        }\n\n        // Execute different commands based on whether 'o', 'a', or 'e' is found\n        if (hasOAE) {\n            std::cout << "String \"" << str << "\" contains 'o', 'a', or 'e'.\n";\n            // Add your command here if the string contains 'o', 'a', or 'e'\n        } else {\n            std::cout << "String \"" << str << "\" does not contain 'o', 'a', or 'e'.\n";\n            // Add your command here if the string does NOT contain 'o', 'a', or 'e'\n        }\n    }\n\n    return 0;\n}