#include <windows.h>\n#include <iostream>\n\nint main() {\n    HKEY hKey;\n    // Accessing 64-bit registry key from a 32-bit application\n    if (RegOpenKeyEx(HKEY_LOCAL_MACHINE, TEXT("SOFTWARE\\Microsoft"), 0, KEY_READ | KEY_WOW64_64KEY, &hKey) == ERROR_SUCCESS) {\n        std::cout << "Accessed 64-bit registry key from a 32-bit application." << std::endl;\n        RegCloseKey(hKey);\n    }\n\n    // Accessing 32-bit registry key\n    if (RegOpenKeyEx(HKEY_LOCAL_MACHINE, TEXT("SOFTWARE\\Wow6432Node\\Microsoft"), 0, KEY_READ, &hKey) == ERROR_SUCCESS) {\n        std::cout << "Accessed 32-bit registry key." << std::endl;\n        RegCloseKey(hKey);\n    }\n\n    return 0;\n}