#include <iostream>\nusing namespace std;\n\n// Function to print an array\nvoid printArray(int arr[], int size) {\n    cout << "Array: { ";\n    for (int i = 0; i < size; ++i)\n        cout << arr[i] << (i < size - 1 ? ", " : "");\n    cout << " }" << endl;\n}\n\n// Define a struct\nstruct MyStruct {\n    int id;\n    string name;\n};\n\n// Function to print a struct\nvoid printStruct(const MyStruct &s) {\n    cout << "MyStruct: { id = " << s.id << ", name = \"" << s.name << "\" }" << endl;\n}\n\nint main() {\n    int myArray[] = {1, 2, 3, 4, 5};\n    printArray(myArray, 5);\n\n    MyStruct myStruct = {101, "Sample"};\n    printStruct(myStruct);\n\n    return 0;\n}