#include <iostream>\n#include <fstream>  // Include this header for file stream operations\n#include <string>   // Use std::string instead of char for filenames\n\nusing namespace std;\n\nint main() {\n    string filename;  // Use string to hold the filename\n    cout << "Please make sure the document is in the same folder as the program, thank you!" << endl;\n    cout << "Please input document name: ";\n    cin >> filename;\n    \n    ifstream infile(filename);  // Create an ifstream object and open the file\n\n    if (!infile.is_open()) {  // Check if the file opened successfully\n        cerr << "Could not open file: " << filename << endl;\n        return 1;  // Exit with error code if file could not be opened\n    }\n\n    while (infile.good()) {  // Instead of checking eof() in loop, check if file is good\n        char c = infile.get();  // Read character from the file\n        if (!infile.eof()) {    // Check if we reached end of file after reading\n            cout.put(c);        // Output the character to console\n        }\n    }\n\n    infile.close();  // Close the file when done\n\n    getchar();  // Wait for user input before closing window (for Visual C++ Express)\n    return 0;\n}