// Using pointer\nvoid modifyWithPointer(int *a) {\n    if (a != nullptr) {\n        *a = 10; // Dereference the pointer to modify the value\n    }\n}\n\n// Using reference\nvoid modifyWithReference(int &a) {\n    a = 20; // Directly modify the referenced variable\n}\n\nint main() {\n    int b = 5;\n    \n    modifyWithPointer(&b); // Pass the address of b\n    std::cout << "After pointer modification: " << b << std::endl; // Outputs: 10\n    \n    modifyWithReference(b); // Directly pass b\n    std::cout << "After reference modification: " << b << std::endl; // Outputs: 20\n\n    return 0;\n}