#include <iostream>\n#include <ncurses.h>\n\nint main() {\n    // Initialize ncurses\n    initscr();\n    noecho();  // Don't echo keystrokes to the screen\n    cbreak();  // Get input character-by-character, not line-by-line\n\n    printw("Press any key...");\n    refresh();\n\n    int ch = getch();  // Wait for a key press\n\n    printw("\nKey pressed: %d", ch);\n    refresh();\n    getch();  // Wait for another key press to exit\n\n    // Clean up\n    endwin();\n\n    return 0;\n}