#include <iostream>\n#include <windows.h>\n\nclass Console {\npublic:\n    static void Print(const std::string& message) {\n        std::cout << message;\n    }\n\n    static void Warning(const std::string& message) {\n        SetColor(14); // Yellow color code in Windows console\n        std::cout << message;\n        ResetColor();\n    }\n\n    static void Error(const std::string& message) {\n        SetColor(12); // Red color code in Windows console\n        std::cout << message;\n        ResetColor();\n    }\n\nprivate:\n    static void SetColor(int textColor) {\n        HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);\n        SetConsoleTextAttribute(hConsole, textColor);\n    }\n\n    static void ResetColor() {\n        SetColor(7); // Default color code in Windows console\n    }\n};\n\nint main() {\n    Console::Print("This is a non-coloured message\n");\n    Console::Warning("This is a YELLOW warning message\n");\n    Console::Error("This is a RED error message\n");\n\n    return 0;\n}