#include <vector>\n#include <algorithm> // For std::lower_bound and std::upper_bound\n\nvoid subarray(const double a, const double b, std::vector<double>& x, std::vector<double>& sub) {\n    // Find the first element that is not less than 'a'\n    auto start = std::lower_bound(x.begin(), x.end(), a);\n    \n    // Find the first element greater than 'b'\n    auto end = std::upper_bound(start, x.end(), b);\n    \n    // Copy the range from start to end into sub\n    sub.assign(start, end);\n}