#include <iostream>\n#include <vector>\nusing namespace std;\n\n// Helper function to convert an integer vector to a string\nstring Vec2Str(const vector<int>& tag) {\n    string strTag;\n    for (int val : tag) {\n        strTag += static_cast<char>('0' + val);\n    }\n    return strTag;\n}\n\nvoid generateNeighbors(const vector<int>& seed, int depth, int maxDepth, vector<int>& current, vector<string>& results, int pos = 0) {\n    if (depth > maxDepth) {\n        results.push_back(Vec2Str(current));\n        return;\n    }\n\n    for (int i = pos; i < seed.size(); ++i) {\n        for (int digit = 0; digit <= 3; ++digit) {\n            if (seed[i] != digit) {\n                int original = current[i];\n                current[i] = digit;\n                generateNeighbors(seed, depth + 1, maxDepth, current, results, i + 1);\n                current[i] = original;\n            }\n        }\n    }\n}\n\nint main() {\n    vector<int> numTag{0, 0, 1}; // Example seed\n\n    int TagLen = numTag.size();\n    vector<string> neighbors;\n\n    for (int depth = 1; depth <= 2; ++depth) {\n        vector<int> current(numTag);\n        generateNeighbors(numTag, 0, depth, current, neighbors);\n    }\n\n    for (const string& neighbor : neighbors) {\n        cout << neighbor << endl;\n    }\n\n    return 0;\n}