#include <list>\n#include <algorithm>\n\nstruct CMyClass {\n    void* Ptr;\n    int Var;\n\n    // You might want to add constructors and comparison operators for better usability\n};\n\nbool is_equal(const CMyClass& A, const CMyClass& B) {\n    return (A.Ptr == B.Ptr && A.Var == B.Var);\n}\n\nvoid foo(std::list<CMyClass>& lst, const CMyClass& Bad) {\n    lst.remove_if([&Bad](const CMyClass& elem) { return is_equal(elem, Bad); });\n}