#include <iostream>\n#include <cstdlib>\n\nint main() {\n    // Path to the wkhtmltopdf executable\n    std::string wkhtmlPath = "path\\to\\wkhtmltopdf.exe";\n    \n    // HTML file and output PDF file paths\n    std::string htmlFile = "example.html"; \n    std::string pdfFile = "output.pdf";\n\n    // Command to convert HTML to PDF\n    std::string command = wkhtmlPath + " " + htmlFile + " " + pdfFile;\n\n    // Execute the command\n    int result = std::system(command.c_str());\n\n    if (result == 0) {\n        std::cout << "PDF created successfully!" << std::endl;\n    } else {\n        std::cerr << "Failed to create PDF." << std::endl;\n    }\n\n    return 0;\n}