#include <iostream>\n\n// Define a type for the callback function\ntypedef void (*CallbackFunc)(int);\n\n// Global pointer to hold the reference to LabVIEW's callback function\nstatic CallbackFunc g_callback = nullptr;\n\nextern "C" __declspec(dllexport) void SetCallback(CallbackFunc cb) {\n    g_callback = cb;\n}\n\nextern "C" __declspec(dllexport) void TriggerCallback() {\n    if (g_callback) {\n        // Example data being sent back to LabVIEW\n        int data = 42;\n        g_callback(data);\n    }\n}