#include <limits>\n#include <iostream>\n#include <cassert>\n\nint main() {\n    unsigned long value1 = LONG_MAX;\n    std::cout << "value1: " << value1 << std::endl;\n\n    assert(value1 <= USHRT_MAX && "Possible data loss when converting from long to short.");\n    \n    unsigned short value2 = static_cast<unsigned short>(value1);\n    std::cout << "value2: " << value2 << std::endl;\n\n    return 0;\n}