#include <iostream>\n#include <fstream>\n#include <sstream>\n\nint main() {\n    std::ifstream file("input.txt");\n    if (!file.is_open()) {\n        std::cerr << "Failed to open file.\n";\n        return 1;\n    }\n\n    std::string word;\n    while (file >> word) { // Reads a word delimited by whitespace\n        std::cout << word << "\n";\n\n        // Check if the next character is a newline\n        char peekChar = file.peek();\n        if (peekChar == '\n') {\n            std::cout << "[NEW LINE]\n";\n            file.get(); // Consume the newline character\n        }\n    }\n\n    // Check for a newline at the end of the file after last word\n    if (file.eof() && file.gcount()) {\n        std::cout << "[NEW LINE]\n";\n    }\n\n    return 0;\n}