// Receiver Example (Process for VendorLibB)\n#include <windows.h>\n#include <iostream>\n\nvoid readMessage(HANDLE pipe) {\n    char message[1024];\n    DWORD bytesRead;\n\n    if (ReadFile(pipe, message, sizeof(message) - 1, &bytesRead, NULL)) {\n        message[bytesRead] = '\0';\n        std::cout << "Received: " << message << std::endl;\n    } else {\n        std::cerr << "Error reading from pipe" << std::endl;\n    }\n}\n\nint main() {\n    HANDLE hPipe = CreateNamedPipe(\n        TEXT("\\\\.\\pipe\\MyPipe"), \n        PIPE_ACCESS_INBOUND,\n        PIPE_TYPE_MESSAGE | PIPE_READMODE_MESSAGE | PIPE_WAIT,   \n        1,              \n        0,             \n        0,            \n        5000,           \n        NULL);            \n\n    if (hPipe == INVALID_HANDLE_VALUE) {\n        std::cerr << "Failed to create inbound pipe instance." << std::endl;\n        return 1;\n    }\n\n    std::cout << "Waiting for client connection..." << std::endl;\n\n    BOOL connectSuccess = ConnectNamedPipe(hPipe, NULL) ? TRUE : (GetLastError() == ERROR_PIPE_CONNECTED);\n    \n    if (!connectSuccess) {\n        std::cerr << "Failed to connect to pipe." << std::endl;\n        return 1;\n    }\n\n    readMessage(hPipe);\n\n    DisconnectNamedPipe(hPipe);\n    CloseHandle(hPipe);\n\n    return 0;\n}