#include <yaml-cpp/yaml.h> // This header is necessary to work with YAML-CPP\n\nint main() {\n    YAML::Emitter out;\n\n    if (!out) throw std::runtime_error("Failed to create YAML emitter!");\n\n    out << YAML::BeginMap;\n    out << YAML::Key << "name";\n    out << YAML::Value << "John Doe";\n\n    out << YAML::Key << "age";\n    out << YAML::Value << 30;\n\n    out << YAML::Key << "children";\n    out << YAML::Value << YAML::BeginSeq; // starts a sequence node\n    out << "Jane Doe";\n    out << "Doe Junior";\n    out << YAML::EndSeq;\n\n    out << YAML::EndMap;\n    \n    std::cout << out.c_str() << "\n"; // Output the serialized document\n    \n    return 0;\n}