#include <iostream>\nusing namespace std;\n\nbool doOverlap(int x1, int y1, int width1, int height1, \n               int x2, int y2, int width2, int height2) {\n    // Calculate right and bottom edges of rectangles\n    int r1_right = x1 + width1;\n    int r1_bottom = y1 - height1;  // Assuming y increasing upwards\n    int r2_right = x2 + width2;\n    int r2_bottom = y2 - height2;\n\n    // If one rectangle is to the left of the other\n    if (x1 > r2_right || x2 > r1_right) {\n        return false;\n    }\n\n    // If one rectangle is above the other\n    if (y1 < r2_bottom || y2 < r1_bottom) {\n        return false;\n    }\n\n    // Rectangles overlap\n    return true;\n}\n\nint main() {\n    int x1, y1, width1, height1;\n    int x2, y2, width2, height2;\n\n    cout << "Enter x, y, width, and height for Rectangle 1: ";\n    cin >> x1 >> y1 >> width1 >> height1;\n\n    cout << "Enter x, y, width, and height for Rectangle 2: ";\n    cin >> x2 >> y2 >> width2 >> height2;\n\n    if (doOverlap(x1, y1, width1, height1, x2, y2, width2, height2)) {\n        cout << "Rectangles overlap." << endl;\n    } else {\n        cout << "Rectangles do not overlap." << endl;\n    }\n\n    return 0;\n}