#include <QSqlDatabase>\n#include <QSqlQuery>\n#include <QDebug>\n\nvoid insertRecord(QSqlDatabase &db, const QString &name, const QVariant &email) {\n    QSqlQuery query(db);\n    query.prepare("INSERT INTO users (name, email) VALUES (?, ?)");\n    query.addBindValue(name);\n\n    // Check if the email is null and bind accordingly\n    if (email.isNull()) {\n        query.bindValue(1, QVariant(QVariant::Null));\n    } else {\n        query.bindValue(1, email);\n    }\n\n    if (!query.exec()) {\n        qDebug() << "Failed to insert record:" << query.lastError().text();\n    }\n}