#include <iostream>\n#include <cstdlib>\n\nint main() {\n    // Command to convert HTML to PDF using wkhtmltopdf\n    std::string command = "wkhtmltopdf example.html output.pdf";\n    \n    // Execute the command\n    int ret = system(command.c_str());\n    \n    if (ret == 0) {\n        std::cout << "PDF created successfully." << std::endl;\n    } else {\n        std::cerr << "Error creating PDF." << std::endl;\n    }\n    \n    return 0;\n}