#include <atomic>\n#include <thread>\n\n// Atomic variables\nstd::atomic<int> x{0};\nstd::atomic<int> y{0};\n\nvoid writer_thread() {\n    x.store(1, std::memory_order_seq_cst); // Write A\n    y.store(2, std::memory_order_seq_cst); // Write B\n}\n\nvoid reader_thread() {\n    int a = y.load(std::memory_order_seq_cst); // Read C\n    int b = x.load(std::memory_order_seq_cst); // Read D\n}