// Example of a simple function in a COM Component\nSTDMETHODIMP CExample::MyMethod(BSTR inputString, /* [out] */ BSTR* outputString) {\n    if (!inputString || !outputString) return E_POINTER;\n    \n    // Simulate processing the string\n    std::wstring processedStr = L"Processed: " + std::wstring(inputString);\n    \n    *outputString = SysAllocString(processedStr.c_str());\n    if (*outputString == nullptr) {\n        return E_OUTOFMEMORY;\n    }\n    return S_OK;\n}