#include <iostream>\n\nbool IsBitTrue(int number, int bitPosition) {\n    // Right shift the number by (bitPosition - 1) and check if the least significant bit is 1\n    return (number >> (bitPosition - 1)) & 1;\n}\n\nint main() {\n    std::cout << std::boolalpha; // To print 'true' or 'false' instead of '1' or '0'\n    std::cout << "IsBitTrue(30, 1): " << IsBitTrue(30, 1) << std::endl; // Output: false\n    std::cout << "IsBitTrue(30, 2): " << IsBitTrue(30, 2) << std::endl; // Output: true\n    std::cout << "IsBitTrue(30, 3): " << IsBitTrue(30, 3) << std::endl; // Output: true\n    return 0;\n}