#include "tinyxml2.h"\n#include <iostream>\n#include <fstream>\n\nusing namespace tinyxml2;\n\nint main() {\n    XMLDocument doc;\n    \n    // Create the root element\n    XMLElement *root = doc.NewElement("html");\n    doc.InsertFirstChild(root);\n    \n    // Add head element\n    XMLElement *head = doc.NewElement("head");\n    root->InsertEndChild(head);\n\n    XMLElement *title = doc.NewElement("title");\n    XMLText *textNode = doc.NewText("My Web Page");\n    title->InsertEndChild(textNode);\n    head->InsertEndChild(title);\n\n    // Add body element\n    XMLElement *body = doc.NewElement("body");\n    root->InsertEndChild(body);\n\n    XMLElement *h1 = doc.NewElement("h1");\n    textNode = doc.NewText("Welcome to My Web Page");\n    h1->InsertEndChild(textNode);\n    body->InsertEndChild(h1);\n\n    XMLElement *p = doc.NewElement("p");\n    textNode = doc.NewText("This is a paragraph.");\n    p->InsertEndChild(textNode);\n    body->InsertEndChild(p);\n\n    // Save the document to a file\n    doc.SaveFile("example.html");\n\n    return 0;\n}