#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <vector>\n\nstd::vector<std::string> readNextLine(std::ifstream& file) {\n    std::string line;\n    if (!std::getline(file, line)) {\n        return {}; // Return an empty vector if no more lines are found\n    }\n    std::vector<std::string> fields;\n    std::stringstream ss(line);\n    std::string field;\n    while (std::getline(ss, field, ',')) {\n        fields.push_back(field);\n    }\n    return fields;\n}\n\nint main() {\n    std::ifstream file("data.csv");\n    if (!file.is_open()) {\n        std::cerr << "Failed to open file." << std::endl;\n        return 1;\n    }\n\n    std::vector<std::string> lineData;\n    while (!(lineData = readNextLine(file)).empty()) {\n        for (const auto& field : lineData) {\n            std::cout << "[" << field << "] ";\n        }\n        std::cout << std::endl;\n    }\n\n    file.close();\n    return 0;\n}