#include <iostream>\n\ntemplate <char... c>\nstruct mystruct {\n    static void go() {\n        char arr[] = {c..., 0}; // Null-terminate the array\n        std::cout << arr << std::endl;\n    }\n};\n\n// Macro to expand string into template parameters\n#define MYSTRUCT(str) mystruct<str[0], str[1], str[2], str[3]>\n\nint main() {\n    MYSTRUCT("asdf")::go(); // Output: asdf\n    return 0;\n}