#include <iostream>\n#include <map>\n#include <sstream>\n#include <string>\n#include <functional>\n\n// Function prototypes for menu items\nvoid addCommand();\nvoid exitCommand();\n\nint main() {\n    // Map of commands to functions\n    std::map<std::string, std::function<void()>> commands = {\n        {"add",   addCommand},\n        {"exit",  exitCommand}\n    };\n\n    std::string input;\n    bool running = true;\n\n    while (running) {\n        std::cout << "Enter a command ('help' for options): ";\n        std::cin >> input;\n\n        if (input == "help") {\n            std::cout << "'add' - Adds 1 and 2\n";\n            std::cout << "'exit' - Exits the program\n";\n        } else if (commands.find(input) != commands.end()) {\n            // Execute command\n            commands[input]();\n            running = (input != "exit"); // Exit condition\n        } else {\n            std::cout << "Unknown command!\n";\n        }\n    }\n\n    return 0;\n}\n\nvoid addCommand() {\n    std::cout << "1 + 2 = " << 1 + 2 << "\n";\n}\n\nvoid exitCommand() {\n    std::cout << "Exiting...\n";\n}