#include <iostream>\n#include "cgicc/CgiDefs.h"\n#include "cgicc/Cgicc.h"\n#include "cgicc/HTTPHTMLHeader.h"\n#include "cgicc/HTMLClasses.h"\nusing namespace std;\nusing namespace cgicc;\n\nint main(){\n    try {\n        Cgicc formData;\n\n        // Send HTTP header to the browser\n        cout << HTTPHTMLHeader() << HTMLDoctype(HTMLDoctype::eStrict) <<\n             html() <<\n             head(title("Sample CGI Program")).set("lang", "en").set("dir", "ltr") <<\n             body() <<\n             h1("Welcome to My Sample CGI Program!") <<\n             endl;\n\n        // Close the HTML document\n        cout << body() << html();\n    }\n    catch(exception& e) {\n        // Handle any errors that may have occurred\n        cerr << "Exception: " << e.what() << "\n";\n        return 1;\n    }\n\n    return 0;\n}