// Include necessary headers\n#include <windows.h>\n\nextern "C" __declspec(dllexport) HBITMAP LoadBitmapFromDll() {\n    // Get the handle to our module (DLL)\n    HMODULE hModule = ::GetModuleHandle("YourDllName.dll");\n\n    // Attempt to load a bitmap resource by its ID\n    HBITMAP hBitmap = ::LoadBitmap(hModule, MAKEINTRESOURCE(IDB_YOUR_BITMAP_RESOURCE_ID));\n    \n    if (!hBitmap) {\n        DWORD err = GetLastError();  // Optionally log/get error code\n    }\n\n    return hBitmap;\n}