#include <windows.h>\n#include <lmaccess.h>\n#include <iostream>\n\nvoid GetPasswordPolicy() {\n    USES_CONVERSION;\n    NET_USER_POLICY_INFO* pUserPolicyInfo = NULL;\n    DWORD dwResult;\n\n    dwResult = NetUserGetPolicies(NULL, NULL, &pUserPolicyInfo);\n    if (dwResult == NERR_Success) {\n       (std::cout << "Minimum password length: " << pUserPolicyInfo->usr_logon_attempts.LogonHours.dwMinutesPerHour\n                  << "\nMinimum password age: " << pUserPolicyInfo->usr_min_passwd_len\n                  << "\nMaximum password age: " << pUserPolicyInfo->usr_max_passwd_age / -86400\n                  << "\nPassword history length: " << pUserPolicyInfo->usr_pass_hist_len\n                  << "\nMinimum account name length: " << pUserPolicyInfo->usr_min_name_len\n                  << "\nMaximum logon attempts: " << pUserPolicyInfo->usr_logon_attempts.dwNumberOfHours\n                  << "\nReset count time: " << pUserPolicyInfo->usr_reset_count_minutes / -60 << std::endl);\n        NetApiBufferFree(pUserPolicyInfo);\n    }\n}\n\nint main() {\n    GetPasswordPolicy();\n    return 0;\n}