// In your DLL header file (.h)\n#include <iostream>\n\nenum class ErrorCode {\n    Success,\n    Error1,\n    Error2\n};\n\nErrorCode performOperation();\n\n// In your DLL source file (.cpp)\n#include "yourLib.h"\n\nErrorCode performOperation() {\n    try {\n        // some logic that may throw exceptions\n        if (/* some condition */) {\n            throw std::runtime_error("Operation failed");\n        }\n    } catch (...) {\n        return ErrorCode::Error1;  // map the exception to an error code\n    }\n    return ErrorCode::Success;\n}\n\n// In your main application source file (.cpp)\n#include <iostream>\n#include "yourLib.h"\n\nint main() {\n    ErrorCode result = performOperation();\n    if (result != ErrorCode::Success) {\n        std::cerr << "An error occurred: " << static_cast<int>(result) << std::endl;\n    } else {\n        std::cout << "Operation succeeded." << std::endl;\n    }\n    return 0;\n}