// Define constants for WM_KEYDOWN lParam\nconst int KEYEVENTF_EXTENDEDKEY = 0x0001; // If set, the key is an extended key\nconst int KEYEVENTF_KEYUP       = 0x0002; // If set, the key is being released\n\n// Function to construct LPARAM for WM_KEYDOWN/WM_KEYUP messages\nint MakeLParamForKeyDown(int nScanCode, bool bExtended) {\n    return ((bExtended ? KEYEVENTF_EXTENDEDKEY : 0) << 24) | (nScanCode << 16);\n}\n\n// Usage: Posting a WM_KEYDOWN message with the correct LPARAM for an up arrow key\nint lParam = MakeLParamForKeyDown(48, true); // Scan code 48 for UP ARROW and extended key\nWinApi.PostMessage(InsideLobbyHandle, WinApi.WM_KEYDOWN, (int)WinApi.VK_UP, lParam);