#include <boost/thread.hpp>\n#include <iostream>\n\nvoid listenForMulticast() {\n    // Your multicast listening logic here\n    std::cout << "Listening for multicasts..." << std::endl;\n}\n\nint main() {\n    try {\n        boost::thread multicastThread(listenForMulticast);\n        \n        // Ensure the main thread doesn't exit immediately, causing the application to be unloaded\n        while (true) {\n            // Optionally add logic here to run periodically or check for conditions\n            std::this_thread::sleep_for(std::chrono::seconds(1));\n        }\n    } catch (...) {\n        std::cerr << "An unexpected error occurred." << std::endl;\n        return 1;   // Exit with a non-zero status in case of an exception\n    }\n\n    return 0;\n}